            // 设置连接属性
            Properties connectionProps = createConnectionProperties(username, password);

            // 记录连接尝试
            LOGGER.log(Level.INFO, "尝试连接到数据库: {0}", sanitizeUrl(url));

            // 设置登录超时
            DriverManager.setLoginTimeout(DEFAULT_CONNECTION_TIMEOUT);

            // 建立连接
            connection = DriverManager.getConnection(url, connectionProps);

            // 验证连接是否有效
            if (!isConnectionValid(connection)) {
                throw new SQLException("创建的数据库连接无效");
            }

            // 设置连接属性
            configureConnection(connection);

            LOGGER.log(Level.INFO, "成功连接到数据库");

            return connection;

        } catch (SQLException e) {
            // 记录错误信息
            LOGGER.log(Level.SEVERE, "数据库连接失败: " + e.getMessage(), e);

            // 确保连接被关闭
            if (connection != null) {
                try {
                    connection.close();
                } catch (SQLException closeEx) {
                    LOGGER.log(Level.WARNING, "关闭失败的连接时出错", closeEx);
                }
            }

            // 重新抛出原始异常
            throw e;
        }
    }

    /**
     * 验证连接参数
     *
     * @param url      数据库连接URL
     * @param username 数据库用户名
     * @param password 数据库密码
     * @throws SQLException 如果参数无效
     */
    private void validateConnectionParameters(String url, String username, String password) throws SQLException {
        if (url == null || url.trim().isEmpty()) {
            throw new SQLException("数据库连接URL不能为空");
        }

        if (username == null || username.trim().isEmpty()) {
            throw new SQLException("数据库用户名不能为空");
        }

        if (password == null) {
            throw new SQLException("数据库密码不能为null");
        }

        // 验证URL格式
        if (!isValidJdbcUrl(url)) {
            throw new SQLException("无效的JDBC URL格式: " + url);
        }
    }

    /**
     * 验证JDBC URL格式
     *
     * @param url JDBC URL
     * @return 如果URL格式有效返回true
     */
    private boolean isValidJdbcUrl(String url) {
        // 支持MySQL的各种URL格式
        return url.startsWith("jdbc:mysql://") || 
               url.startsWith("jdbc:mysql:loadbalance://") ||
               url.startsWith("jdbc:mysql:replication://");
    }

    /**
     * 创建连接属性
     *
     * @param username 用户名
     * @param password 密码
     * @return 连接属性对象
     */
    private Properties createConnectionProperties(String username, String password) {
        Properties props = new Properties();

        // 基本认证信息
        props.setProperty("user", username);
        props.setProperty("password", password);

        // 连接配置
        props.setProperty("connectTimeout", String.valueOf(DEFAULT_CONNECTION_TIMEOUT * 1000));
        props.setProperty("socketTimeout", String.valueOf(DEFAULT_SOCKET_TIMEOUT));

        // 字符编码设置
        props.setProperty("useUnicode", "true");
        props.setProperty("characterEncoding", "UTF-8");
        props.setProperty("connectionCollation", "utf8mb4_unicode_ci");

        // SSL配置（根据需要可以调整）
        props.setProperty("useSSL", "false");
        props.setProperty("allowPublicKeyRetrieval", "true");

        // 其他优化配置
        props.setProperty("autoReconnect", "false"); // 不建议使用自动重连
        props.setProperty("useServerPrepStmts", "true");
        props.setProperty("cachePrepStmts", "true");
        props.setProperty("prepStmtCacheSize", "250");
        props.setProperty("prepStmtCacheSqlLimit", "2048");

        // 时区设置
        props.setProperty("serverTimezone", "UTC");

        return props;
    }

    /**
     * 验证连接是否有效
     *
     * @param connection 数据库连接
     * @return 如果连接有效返回true
     */
    private boolean isConnectionValid(Connection connection) {
        try {
            return connection != null && !connection.isClosed() && connection.isValid(VALIDATION_TIMEOUT);
        } catch (SQLException e) {
            LOGGER.log(Level.WARNING, "验证连接有效性时出错", e);
            return false;
        }
    }

    /**
     * 配置连接属性
     *
     * @param connection 数据库连接
     * @throws SQLException 如果配置失败
     */
    private void configureConnection(Connection connection) throws SQLException {
        // 设置事务隔离级别
        connection.setTransactionIsolation(Connection.TRANSACTION_READ_COMMITTED);

        // 默认设置为自动提交
        connection.setAutoCommit(true);

        // 设置为只读模式（如果需要可以在外部修改）
        connection.setReadOnly(false);
    }

    /**
     * 清理URL中的敏感信息用于日志记录
     *
     * @param url 原始URL
     * @return 清理后的URL
     */
    private String sanitizeUrl(String url) {
        // 移除URL中可能包含的密码信息
        return url.replaceAll("password=[^&]*", "password=***")
                  .replaceAll(":[^:@]*@", ":***@");
    }
}

