        connectionProps.setProperty("user", username);
        connectionProps.setProperty("password", password);

        // 设置安全的连接参数
        connectionProps.setProperty("useSSL", "true");
        connectionProps.setProperty("verifyServerCertificate", "true");
        connectionProps.setProperty("requireSSL", "false");
        connectionProps.setProperty("connectTimeout", String.valueOf(CONNECTION_TIMEOUT * 1000));
        connectionProps.setProperty("socketTimeout", String.valueOf(SOCKET_TIMEOUT * 1000));

        // 禁用危险特性
