        connectionProps.setProperty("allowLoadLocalInfileInPath", "");
        connectionProps.setProperty("autoDeserialize", "false");
        connectionProps.setProperty("allowMultiQueries", "false");

        // 5. 清理URL，移除已有的参数避免覆盖安全设置
        String cleanUrl = cleanUrl(url);

        // 6. 建立连接
        return DriverManager.getConnection(cleanUrl, connectionProps);
    }

    /**
     * 验证输入参数
     */
    private void validateInputs(String url, String username) throws SQLException {
        if (url == null || url.trim().isEmpty()) {
            throw new SQLException("数据库URL不能为空");
        }
        if (username == null || username.trim().isEmpty()) {
            throw new SQLException("用户名不能为空");
        }
        // 限制URL长度，防止过长的输入
        if (url.length() > 500) {
            throw new SQLException("数据库URL长度超过限制");
        }
        // 限制用户名长度
        if (username.length() > 100) {
            throw new SQLException("用户名长度超过限制");
        }
    }

    /**
     * 验证MySQL URL格式
     */
    private boolean isValidMySQLUrl(String url) {
        if (!url.startsWith("jdbc:mysql://")) {
            return false;
        }
        return MYSQL_URL_PATTERN.matcher(url).matches();
    }

    /**
     * 检查URL是否包含危险参数
     */
    private boolean containsDangerousParams(String url) {
        String lowerUrl = url.toLowerCase();
        for (String param : DANGEROUS_PARAMS) {
            if (lowerUrl.contains(param.toLowerCase() + "=true") || 
                lowerUrl.contains(param.toLowerCase() + "=1")) {
                return true;
            }
        }
        return false;
    }

    /**
     * 清理URL，移除查询参数
     */
    private String cleanUrl(String url) {
        int queryIndex = url.indexOf('?');
        if (queryIndex > 0) {
            return url.substring(0, queryIndex);
        }
        return url;
    }
}

