 */
@Service
public class WebPageFetcherService {

    private final RestTemplate restTemplate;

    // 允许的协议白名单
    private static final Set<String> ALLOWED_PROTOCOLS = new HashSet<>(Arrays.asList("http", "https"));

    // 允许的端口白名单
    private static final Set<Integer> ALLOWED_PORTS = new HashSet<>(Arrays.asList(80, 443, 8080, 8443));

    // 内网IP正则表达式
    private static final Pattern PRIVATE_IP_PATTERN = Pattern.compile(
        "^(127\\.|10\\.|172\\.(1[6-9]|2[0-9]|3[01])\\.|192\\.168\\.|169\\.254\\.|::1|[fF][cCdD])"
    );

    // 最大响应大小：10MB
    private static final int MAX_RESPONSE_SIZE = 10 * 1024 * 1024;

    // 请求超时时间：5秒
    private static final int REQUEST_TIMEOUT = 5000;

    @Autowired
    public WebPageFetcherService(RestTemplate restTemplate) {
        // 配置RestTemplate的超时设置
        SimpleClientHttpRequestFactory factory = new SimpleClientHttpRequestFactory();
        factory.setConnectTimeout(REQUEST_TIMEOUT);
        factory.setReadTimeout(REQUEST_TIMEOUT);
        restTemplate.setRequestFactory(factory);
        this.restTemplate = restTemplate;
    }

