     * 根据提供的URL获取其HTML页面内容。
     * 该方法设计用于从公共互联网获取数据。
     *
     * @param url 目标网页的完整URL，例如 "https://www.example.com"
     * @return 返回网页的HTML内容字符串。如果无法访问或发生错误，则返回null。
     */
    public String getPageContent(String url) {
        try {
            // 1. 验证URL格式和协议
            if (!isValidUrl(url)) {
                System.err.println("Invalid URL format or protocol: " + url);
                return null;
            }

            URL parsedUrl = new URL(url);

            // 2. 验证协议
            if (!ALLOWED_PROTOCOLS.contains(parsedUrl.getProtocol().toLowerCase())) {
                System.err.println("Protocol not allowed: " + parsedUrl.getProtocol());
                return null;
            }

            // 3. 验证端口
            int port = parsedUrl.getPort();
            if (port == -1) {
                port = parsedUrl.getDefaultPort();
            }
            if (!ALLOWED_PORTS.contains(port)) {
                System.err.println("Port not allowed: " + port);
                return null;
            }

            // 4. 解析并验证目标IP地址
            String host = parsedUrl.getHost();
            if (!isValidHost(host)) {
                System.err.println("Invalid or private host: " + host);
                return null;
            }

            // 5. 设置安全的请求头
            HttpHeaders headers = new HttpHeaders();
            headers.set("User-Agent", "WebPageFetcher/1.0");
            headers.set("Accept", "text/html,application/xhtml+xml");
            headers.set("Accept-Language", "en-US,en;q=0.9");
            headers.set("Cache-Control", "no-cache");

            HttpEntity<String> entity = new HttpEntity<>(headers);

            // 6. 发送请求并获取响应
            ResponseEntity<String> response = restTemplate.exchange(
                url, 
                HttpMethod.GET, 
                entity, 
                String.class
            );

            // 7. 验证响应大小
            String content = response.getBody();
            if (content != null && content.length() > MAX_RESPONSE_SIZE) {
                System.err.println("Response too large: " + content.length() + " bytes");
                return null;
            }

            return content;

        } catch (MalformedURLException e) {
            System.err.println("Malformed URL: " + url + " - " + e.getMessage());
            return null;
        } catch (RestClientException e) {
            System.err.println("Error fetching content from URL: " + url + " - " + e.getMessage());
            return null;
        } catch (Exception e) {
            System.err.println("Unexpected error: " + e.getMessage());
            return null;
        }
    }

    /**
     * 验证URL格式是否有效
     * @param url 要验证的URL
     * @return 如果URL格式有效返回true，否则返回false
     */
    private boolean isValidUrl(String url) {
        if (url == null || url.trim().isEmpty()) {
            return false;
        }

        // 基本长度限制
        if (url.length() > 2048) {
            return false;
        }

        // 必须以http://或https://开头
        if (!url.toLowerCase().startsWith("http://") && !url.toLowerCase().startsWith("https://")) {
            return false;
        }

        try {
            new URL(url);
            return true;
        } catch (MalformedURLException e) {
            return false;
        }
    }

    /**
     * 验证主机是否为公网地址
     * @param host 主机名或IP地址
     * @return 如果是公网地址返回true，否则返回false
     */
    private boolean isValidHost(String host) {
        if (host == null || host.trim().isEmpty()) {
            return false;
        }

        try {
            // 解析主机名到IP地址
            InetAddress[] addresses = InetAddress.getAllByName(host);

            for (InetAddress address : addresses) {
                String ip = address.getHostAddress();

                // 检查是否为内网IP或特殊IP
                if (isPrivateOrSpecialIP(ip)) {
                    return false;
                }

                // 检查是否为本地回环地址
                if (address.isLoopbackAddress()) {
                    return false;
                }

                // 检查是否为链路本地地址
                if (address.isLinkLocalAddress()) {
                    return false;
                }

                // 检查是否为站点本地地址（私有地址）
                if (address.isSiteLocalAddress()) {
                    return false;
                }

                // 检查是否为多播地址
                if (address.isMulticastAddress()) {
                    return false;
                }
            }

            return true;

        } catch (UnknownHostException e) {
            // 无法解析的主机名，为了安全起见拒绝访问
            return false;
        }
    }

    /**
     * 检查IP是否为私有IP或特殊IP
     * @param ip IP地址字符串
     * @return 如果是私有或特殊IP返回true，否则返回false
     */
    private boolean isPrivateOrSpecialIP(String ip) {
        if (ip == null) {
            return true;
        }

        // 使用正则表达式检查常见的私有IP段
        if (PRIVATE_IP_PATTERN.matcher(ip).find()) {
            return true;
        }

        // 检查特殊的IP段
        String[] parts = ip.split("\\.");
        if (parts.length == 4) {
            try {
                int first = Integer.parseInt(parts[0]);
                int second = Integer.parseInt(parts[1]);

                // 0.0.0.0/8 - 当前网络
                if (first == 0) {
                    return true;
                }

                // 100.64.0.0/10 - 运营商级NAT
                if (first == 100 && second >= 64 && second <= 127) {
                    return true;
                }

                // 169.254.0.0/16 - 链路本地（包括云服务元数据）
                if (first == 169 && second == 254) {
                    return true;
                }

                // 224.0.0.0/4 - 多播
                if (first >= 224 && first <= 239) {
                    return true;
                }

                // 240.0.0.0/4 - 保留
                if (first >= 240) {
                    return true;
                }

            } catch (NumberFormatException e) {
                // 解析失败，为了安全起见认为是私有IP
                return true;
            }
        }

        return false;
    }
}

