            // 设置请求头，模拟浏览器请求
            HttpHeaders headers = new HttpHeaders();
            headers.add("User-Agent", "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36");
            headers.add("Accept", "text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8");
            headers.add("Accept-Language", "zh-CN,zh;q=0.9,en;q=0.8");
            headers.add("Connection", "keep-alive");

            HttpEntity<String> entity = new HttpEntity<>(headers);

            // 发起GET请求
            ResponseEntity<String> response = restTemplate.exchange(
                url,
                HttpMethod.GET,
                entity,
                String.class
            );

            // 检查响应状态
            if (response.getStatusCode().is2xxSuccessful()) {
                String content = response.getBody();
                logger.info("成功抓取网页内容，大小: {} 字符", content != null ? content.length() : 0);
                return content != null ? content : "";
            } else {
                logger.error("抓取网页失败，HTTP状态码: {}", response.getStatusCode());
                throw new RuntimeException("抓取网页失败，HTTP状态码: " + response.getStatusCode());
            }

        } catch (HttpClientErrorException e) {
            // 4xx 客户端错误
            logger.error("客户端请求错误 ({}): {}", e.getStatusCode(), e.getMessage());
            throw new RuntimeException("请求失败: " + e.getStatusCode() + " - " + e.getStatusText(), e);
        } catch (HttpServerErrorException e) {
            // 5xx 服务器错误
            logger.error("服务器错误 ({}): {}", e.getStatusCode(), e.getMessage());
            throw new RuntimeException("服务器错误: " + e.getStatusCode() + " - " + e.getStatusText(), e);
        } catch (ResourceAccessException e) {
            // 网络连接问题
            logger.error("网络连接失败: {}", e.getMessage());
            throw new RuntimeException("无法连接到目标网站: " + e.getMessage(), e);
        } catch (Exception e) {
            // 其他未预期的异常
            logger.error("抓取网页时发生未知错误: {}", e.getMessage(), e);
            throw new RuntimeException("抓取网页失败: " + e.getMessage(), e);
        }
    }
}
