import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.SQLException;
import java.util.Properties;
import java.util.logging.Level;
import java.util.logging.Logger;

/**
 * 数据库连接服务类
 * 提供动态数据库连接功能，支持多种数据库类型
 */
public class DatabaseService {

    private static final Logger logger = Logger.getLogger(DatabaseService.class.getName());

    // 默认连接超时时间（秒）
    private static final int DEFAULT_CONNECTION_TIMEOUT = 30;

    // 默认登录超时时间（秒）
    private static final int DEFAULT_LOGIN_TIMEOUT = 10;

    /**
     * 根据提供的连接参数建立数据库连接
     *
     * @param url 数据库连接URL
     * @param username 数据库用户名
     * @param password 数据库密码
     * @return 数据库连接对象
     * @throws SQLException 如果连接数据库失败
     */
    public Connection connectToDatabase(String url, String username, String password) throws SQLException {
        return connectToDatabase(url, username, password, DEFAULT_CONNECTION_TIMEOUT);
    }

    /**
     * 根据提供的连接参数建立数据库连接（带超时设置）
     *
     * @param url 数据库连接URL
     * @param username 数据库用户名
     * @param password 数据库密码
     * @param timeoutSeconds 连接超时时间（秒）
     * @return 数据库连接对象
     * @throws SQLException 如果连接数据库失败
     */
    public Connection connectToDatabase(String url, String username, String password, int timeoutSeconds) throws SQLException {
        // 参数验证
        validateConnectionParameters(url, username, password);

        logger.info("尝试连接数据库: " + maskUrl(url));

        try {
            // 设置登录超时
            DriverManager.setLoginTimeout(timeoutSeconds > 0 ? timeoutSeconds : DEFAULT_LOGIN_TIMEOUT);

            // 创建连接属性
            Properties connectionProps = createConnectionProperties(username, password, timeoutSeconds);

            // 建立连接
            Connection connection = DriverManager.getConnection(url, connectionProps);

            // 验证连接有效性
            if (!connection.isValid(5)) {
                connection.close();
                throw new SQLException("数据库连接验证失败");
            }

            logger.info("数据库连接建立成功");
            return connection;

        } catch (SQLException e) {
            logger.log(Level.SEVERE, "数据库连接失败: " + e.getMessage(), e);
            throw e;
        }
    }

    /**
     * 验证连接参数
     *
     * @param url 数据库连接URL
     * @param username 数据库用户名
     * @param password 数据库密码
     * @throws SQLException 如果参数无效
     */
    private void validateConnectionParameters(String url, String username, String password) throws SQLException {
        if (url == null || url.trim().isEmpty()) {
            throw new SQLException("数据库连接URL不能为空");
        }

        if (username == null) {
            throw new SQLException("数据库用户名不能为null");
        }

        if (password == null) {
            throw new SQLException("数据库密码不能为null");
        }

        // 验证URL格式
        if (!url.startsWith("jdbc:")) {
            throw new SQLException("无效的数据库连接URL格式，必须以'jdbc:'开头");
        }
    }

    /**
     * 创建连接属性
     *
     * @param username 数据库用户名
     * @param password 数据库密码
     * @param timeoutSeconds 连接超时时间
     * @return 连接属性对象
     */
    private Properties createConnectionProperties(String username, String password, int timeoutSeconds) {
        Properties props = new Properties();
        props.setProperty("user", username);
        props.setProperty("password", password);

        // 设置连接超时（毫秒）
        if (timeoutSeconds > 0) {
            props.setProperty("connectTimeout", String.valueOf(timeoutSeconds * 1000));
            props.setProperty("socketTimeout", String.valueOf(timeoutSeconds * 1000));
        }

        // 设置字符编码
        props.setProperty("useUnicode", "true");
        props.setProperty("characterEncoding", "UTF-8");

        // 设置SSL相关属性（适用于MySQL 8.0+）
        props.setProperty("useSSL", "false");
        props.setProperty("allowPublicKeyRetrieval", "true");

        // 设置时区
        props.setProperty("serverTimezone", "Asia/Shanghai");

        return props;
    }

    /**
     * 屏蔽URL中的敏感信息用于日志记录
     *
     * @param url 原始URL
     * @return 屏蔽敏感信息后的URL
     */
    private String maskUrl(String url) {
        if (url == null) {
            return "null";
        }

        // 简单的密码屏蔽处理
        return url.replaceAll("password=[^&;]*", "password=***");
    }

    /**
     * 测试数据库连接是否可用
     *
     * @param url 数据库连接URL
     * @param username 数据库用户名
     * @param password 数据库密码
     * @return 连接是否成功
     */
    public boolean testConnection(String url, String username, String password) {
        try (Connection connection = connectToDatabase(url, username, password)) {
            return connection != null && connection.isValid(5);
        } catch (SQLException e) {
            logger.log(Level.WARNING, "数据库连接测试失败: " + e.getMessage());
            return false;
        }
    }
}

