        connectionProps.setProperty("user", username);
        connectionProps.setProperty("password", password);

        // 设置连接超时
        DriverManager.setLoginTimeout(timeoutSeconds);

        // 建立连接
        try {
            Connection connection = DriverManager.getConnection(url, connectionProps);

            // 设置连接为非自动提交模式，提高事务控制能力
            connection.setAutoCommit(false);

            return connection;
        } catch (SQLException e) {
            throw new SQLException("无法连接到数据库: " + e.getMessage(), e);
        }
    }

    /**
     * 验证连接参数
     */
    private void validateConnectionParameters(String url, String username, String password, int timeoutSeconds) {
        if (url == null || url.trim().isEmpty()) {
            throw new IllegalArgumentException("数据库URL不能为空");
        }

        if (username == null) {
            throw new IllegalArgumentException("用户名不能为null");
        }

        if (password == null) {
            throw new IllegalArgumentException("密码不能为null");
        }

        if (timeoutSeconds <= 0) {
            throw new IllegalArgumentException("超时时间必须大于0");
