        // 检查驱动是否已加载
        if (loadedDrivers.containsKey(driverClass)) {
            return;
        }

        try {
            Class.forName(driverClass);
            loadedDrivers.put(driverClass, true);
        } catch (ClassNotFoundException e) {
            throw new SQLException("无法加载数据库驱动: " + driverClass, e);
        }
    }

    /**
