        if (zipFilePath == null || zipFilePath.trim().isEmpty()) {
            throw new IllegalArgumentException("ZIP文件路径不能为空");
        }
        if (extractPath == null || extractPath.trim().isEmpty()) {
            throw new IllegalArgumentException("解压路径不能为空");
        }

        File zipFile = new File(zipFilePath);
        if (!zipFile.exists()) {
            throw new FileNotFoundException("ZIP文件不存在: " + zipFilePath);
        }
        if (!zipFile.isFile()) {
            throw new IllegalArgumentException("ZIP文件路径必须指向文件: " + zipFilePath);
        }

        Path extractDir = Paths.get(extractPath).toAbsolutePath();
        if (!Files.exists(extractDir)) {
            Files.createDirectories(extractDir);
        }

        List<String> extractedFiles = new ArrayList<>();
        long totalExtractedSize = 0;

        try (ZipInputStream zipInputStream = new ZipInputStream(new BufferedInputStream(new FileInputStream(zipFile)))) {
            ZipEntry entry;
            while ((entry = zipInputStream.getNextEntry()) != null) {
                // 安全检查：防止ZIP炸弹
                if (entry.getSize() > MAX_FILE_SIZE) {
