import java.util.concurrent.TimeUnit;

public class DatabaseService {

    /**
     * 根据提供的连接参数建立数据库连接
     *
     * @param url 数据库连接URL
     * @param username 数据库用户名
     * @param password 数据库密码
     * @return 数据库连接对象
     * @throws SQLException 如果连接数据库失败
     * @throws IllegalArgumentException 如果输入参数无效
     */
    public Connection connectToDatabase(String url, String username, String password) throws SQLException {
        // 输入验证
        validateInputParameters(url, username, password);

        // 创建连接属性
        Properties connectionProps = createConnectionProperties(username, password);

        // 建立连接
        return DriverManager.getConnection(url, connectionProps);
    }

    /**
     * 验证输入参数
     */
    private void validateInputParameters(String url, String username, String password) {
        if (url == null || url.trim().isEmpty()) {
            throw new IllegalArgumentException("数据库URL不能为空");
        }

        if (!url.startsWith("jdbc:mysql://") && !url.startsWith("jdbc:mariadb://")) {
            throw new IllegalArgumentException("不支持的数据库类型或URL格式不正确");
        }

        if (username == null || username.trim().isEmpty()) {
            throw new IllegalArgumentException("用户名不能为空");
        }

        if (password == null) {
            throw new IllegalArgumentException("密码不能为null");
        }

        // 防止URL注入攻击的基本检查
        if (url.contains(";") || url.contains("--") || url.contains("/*")) {
            throw new IllegalArgumentException("URL包含可疑字符");
        }
    }

    /**
     * 创建安全的连接属性
     */
    private Properties createConnectionProperties(String username, String password) {
        Properties props = new Properties();
        props.setProperty("user", username);
        props.setProperty("password", password);

        // 设置安全相关的连接属性
        props.setProperty("useSSL", "true");
        props.setProperty("requireSSL", "true");
        props.setProperty("verifyServerCertificate", "true");
        props.setProperty("useUnicode", "true");
        props.setProperty("characterEncoding", "UTF-8");

        // 设置超时参数
        props.setProperty("connectTimeout", String.valueOf(TimeUnit.SECONDS.toMillis(30)));
        props.setProperty("socketTimeout", String.valueOf(TimeUnit.SECONDS.toMillis(60)));

        // 防止SQL注入
        props.setProperty("useCursorFetch", "true");
        props.setProperty("allowMultiQueries", "false");
        props.setProperty("allowLoadLocalInfile", "false");

        return props;
    }
}

