package com.example.service;

import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.SQLException;
import java.util.regex.Pattern;

public class DatabaseService {

    // 允许的数据库类型白名单
    private static final Pattern ALLOWED_DB_PATTERN = 
        Pattern.compile("^jdbc:(mysql|postgresql)://[\\w\\-\\.]+(:\\d+)?/\\w+$", Pattern.CASE_INSENSITIVE);

    // SQL注入检测模式
    private static final Pattern SQL_INJECTION_PATTERN = 
        Pattern.compile("([';\"\\\\]|(--)|(\\b(SELECT|INSERT|UPDATE|DELETE|DROP|UNION|EXEC)\\b))", Pattern.CASE_INSENSITIVE);

    /**
     * 根据提供的连接参数建立数据库连接
     *
     * @param url 数据库连接URL
     * @param username 数据库用户名
     * @param password 数据库密码
     * @return 数据库连接对象
     * @throws SQLException 如果连接数据库失败
     * @throws IllegalArgumentException 如果输入参数无效
     */
    public Connection connectToDatabase(String url, String username, String password) throws SQLException {
        // 输入验证
        validateInputParameters(url, username, password);

        // 使用DriverManager建立连接
        return DriverManager.getConnection(url, username, password);
    }

    /**
     * 验证输入参数的安全性
     */
    private void validateInputParameters(String url, String username, String password) {
        if (url == null || url.trim().isEmpty()) {
            throw new IllegalArgumentException("数据库URL不能为空");
        }

        if (username == null || username.trim().isEmpty()) {
            throw new IllegalArgumentException("用户名不能为空");
        }

        // URL格式验证
        if (!ALLOWED_DB_PATTERN.matcher(url).matches()) {
            throw new IllegalArgumentException("无效的数据库URL格式");
        }

        // SQL注入检测
        if (SQL_INJECTION_PATTERN.matcher(url).find()) {
            throw new IllegalArgumentException("URL中包含可疑的SQL注入字符");
        }

        if (SQL_INJECTION_PATTERN.matcher(username).find()) {
            throw new IllegalArgumentException("用户名中包含可疑的SQL注入字符");
        }

        // 密码长度检查（可根据需要调整）
        if (password != null && password.length() > 256) {
            throw new IllegalArgumentException("密码长度超出限制");
        }

        // 检查URL中的敏感信息
        if (url.toLowerCase().contains("password") || url.toLowerCase().contains("pwd")) {
            throw new IllegalArgumentException("URL中不应包含密码信息");
        }
    }

    /**
     * 安全地加载数据库驱动（按需加载）
     */
    private void loadDriverSafely(String url) {
        try {
            // 根据URL自动检测并加载合适的驱动
            if (url.startsWith("jdbc:mysql:")) {
                Class.forName("com.mysql.cj.jdbc.Driver");
            } else if (url.startsWith("jdbc:postgresql:")) {
                Class.forName("org.postgresql.Driver");
            } else {
                throw new IllegalArgumentException("不支持的数据库类型");
            }
        } catch (ClassNotFoundException e) {
            throw new IllegalArgumentException("数据库驱动加载失败: " + e.getMessage());
