            System.err.println("Security violation for URL: " + url + " - " + e.getMessage());
            return null;
        } catch (RestClientException e) {
            // 在实际应用中，这里会记录更详细的日志。
            System.err.println("Error fetching content from URL: " + url + " - " + e.getMessage());
            return null;
        }
    }

    /**
     * 验证URL的安全性，防止SSRF攻击
     */
    private void validateUrl(String urlString) {
        try {
            URL url = new URL(urlString);

            // 验证协议
            String protocol = url.getProtocol().toLowerCase();
            if (!ALLOWED_PROTOCOLS.contains(protocol)) {
                throw new SecurityException("Protocol not allowed: " + protocol);
            }

            // 验证主机名
            String host = url.getHost().toLowerCase();
            if (!isAllowedHost(host)) {
                throw new SecurityException("Host not allowed: " + host);
            }

            // 验证端口（只允许标准端口）
