        if (filePath.trim().isEmpty()) {
            throw new IllegalArgumentException("文件路径不能为空");
        }

        // 处理输入路径
        Path inputPath = Paths.get(filePath).normalize();

        // 安全检查：防止路径遍历攻击
        if (inputPath.startsWith("..") || !inputPath.normalize().equals(inputPath)) {
