        if (!Files.exists(targetPath)) {
            throw new IOException("文件不存在: " + targetPath);
        }

        if (!Files.isRegularFile(targetPath)) {
            throw new IOException("路径不是普通文件: " + targetPath);
        }

        // 检查文件大小（可选：防止读取超大文件）
