import java.io.IOException;
import java.nio.charset.StandardCharsets;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.StringJoiner;

public class FileService {

    /**
     * 根据传入的文件路径读取文件内容
     *
     * @param filePath 文件路径（相对于 /tmp/upload/ 目录）
     * @return 文件的完整内容作为字符串
     * @throws IOException 如果文件不存在或读取失败
     */
    public String readFileContent(String filePath) throws IOException {
        // 基础目录
        Path baseDir = Paths.get("/tmp/upload/").toAbsolutePath().normalize();

        // 验证基础目录存在且是目录
        if (!Files.exists(baseDir) || !Files.isDirectory(baseDir)) {
            throw new IOException("基础目录不存在或不是目录: " + baseDir);
        }

        // 用户传入的文件路径
        if (filePath == null || filePath.trim().isEmpty()) {
            throw new IOException("文件路径不能为空");
        }

        Path inputPath = Paths.get(filePath).normalize();

        // 防止路径遍历攻击：确保规范化后的路径不以 ".." 开头且不包含 ".." 路径组件
        if (inputPath.startsWith("..") || inputPath.toString().contains("..")) {
            throw new IOException("无效的文件路径: " + filePath);
        }

        // 拼接完整路径
        Path targetPath = baseDir.resolve(inputPath).normalize();

        // 再次验证目标路径是否在基础目录内（双重检查）
        if (!targetPath.startsWith(baseDir)) {
            throw new IOException("访问路径越界: " + filePath);
        }

        // 检查文件是否存在且是普通文件
        if (!Files.exists(targetPath)) {
            throw new IOException("文件不存在: " + filePath);
        }

        if (!Files.isRegularFile(targetPath)) {
            throw new IOException("路径不是普通文件: " + filePath);
        }

        // 检查文件大小，避免读取过大文件（可选：限制为10MB）
        long fileSize = Files.size(targetPath);
        if (fileSize > 10 * 1024 * 1024) { // 10MB限制
            throw new IOException("文件过大，无法读取: " + filePath + " (" + fileSize + " bytes)");
        }

        // 读取文件内容
        StringJoiner contentJoiner = new StringJoiner(System.lineSeparator());

        try (BufferedReader reader = Files.newBufferedReader(targetPath, StandardCharsets.UTF_8)) {
            String line;
            while ((line = reader.readLine()) != null) {
                contentJoiner.add(line);
            }
        } catch (IOException e) {
            throw new IOException("读取文件失败: " + filePath, e);
        }

        return contentJoiner.toString();
    }
}

