    private void validateInputParameters(String url, String username, String password) {
        if (url == null || url.trim().isEmpty()) {
            throw new IllegalArgumentException("数据库URL不能为空");
        }

        if (username == null || username.trim().isEmpty()) {
            throw new IllegalArgumentException("用户名不能为空");
        }

        // URL协议白名单验证
        if (!ALLOWED_PROTOCOLS.matcher(url).matches()) {
            throw new IllegalArgumentException("不支持的数据库协议");
        }

        // 基本的URL格式验证
