        connectionProps.setProperty("user", username);
        connectionProps.setProperty("password", password);

        // 设置一些合理的连接超时和socket超时参数
        connectionProps.setProperty("connectTimeout", "5000"); // 5秒连接超时
        connectionProps.setProperty("socketTimeout", "30000"); // 30秒socket超时

        try {
            // 现代JDBC驱动会自动注册，不需要显式加载驱动类
            return DriverManager.getConnection(url, connectionProps);
        } catch (SQLException e) {
            // 记录日志（实际项目中应该添加日志记录）
            System.err.println("数据库连接失败: " + e.getMessage());
            throw e; // 直接抛出SQLException，不进行包装
        }
    }

    /**
     * 重载方法：使用Properties提供更灵活的连接配置
     *
     * @param url 数据库连接URL
     * @param connectionProps 连接属性
     * @return 数据库连接对象
     * @throws SQLException 如果连接数据库失败
     */
    public Connection connectToDatabase(String url, Properties connectionProps) throws SQLException {
        if (url == null || url.trim().isEmpty()) {
            throw new IllegalArgumentException("数据库URL不能为空");
        }

        if (connectionProps == null) {
            throw new IllegalArgumentException("连接属性不能为null");
        }

        try {
            return DriverManager.getConnection(url, connectionProps);
        } catch (SQLException e) {
            System.err.println("数据库连接失败: " + e.getMessage());
