        try (InputStream inputStream = new java.io.FileInputStream(configFile)) {
            Document document = builder.parse(inputStream);
            document.getDocumentElement().normalize();
            return document;
        }
    }

    /**
     * 配置DocumentBuilderFactory的安全特性，防止XXE攻击
     * 
     * @param factory DocumentBuilderFactory实例
     */
    private void configureSecurity(DocumentBuilderFactory factory) {
        try {
            // 禁用外部实体解析，防止XXE攻击
