/**
 * 配置服务类
 * 用于加载并标准化XML格式的配置文件
 */
public class ConfigService {

    /**
     * 根据用户指定的配置文件路径加载XML配置，并进行身份转换。
     *
     * @param configPath 配置文件路径
     * @return 标准化后的XML文档对象
     * @throws IllegalArgumentException 当文件路径为空或无效时抛出
     * @throws IOException 当文件不存在或读取失败时抛出
     * @throws TransformerException 当XML转换失败时抛出
     */
    public Document loadConfig(String configPath) throws IOException, TransformerException {
        if (configPath == null || configPath.trim().isEmpty()) {
            throw new IllegalArgumentException("配置文件路径不能为空");
        }

        File configFile = new File(configPath);
        if (!configFile.exists()) {
            throw new IOException("配置文件不存在: " + configPath);
        }
        if (!configFile.isFile()) {
            throw new IOException("配置路径不是一个文件: " + configPath);
        }
        if (!configFile.canRead()) {
            throw new IOException("配置文件不可读: " + configPath);
        }

        // 使用安全的DocumentBuilder先解析XML，禁用外部实体
        Document sourceDoc = parseXmlSafely(configFile);

        // 创建安全的TransformerFactory
        TransformerFactory factory = createSecureTransformerFactory();

        // 创建一个身份转换器
        Transformer transformer = factory.newTransformer();

        DOMSource source = new DOMSource(sourceDoc);
        DOMResult result = new DOMResult();

        transformer.transform(source, result);

        return (Document) result.getNode();
    }

    /**
     * 安全地解析XML文件，禁用外部实体防止XXE攻击
     */
    private Document parseXmlSafely(File xmlFile) throws IOException {
        try {
            DocumentBuilderFactory dbf = DocumentBuilderFactory.newInstance();

            // 禁用外部实体
            dbf.setFeature(XMLConstants.FEATURE_SECURE_PROCESSING, true);
            dbf.setFeature("http://apache.org/xml/features/disallow-doctype-decl", true);
            dbf.setFeature("http://xml.org/sax/features/external-general-entities", false);
            dbf.setFeature("http://xml.org/sax/features/external-parameter-entities", false);
            dbf.setFeature("http://apache.org/xml/features/nonvalidating/load-external-dtd", false);
            dbf.setXIncludeAware(false);
            dbf.setExpandEntityReferences(false);

            DocumentBuilder builder = dbf.newDocumentBuilder();
            return builder.parse(xmlFile);
        } catch (Exception e) {
            throw new IOException("XML解析失败: " + e.getMessage(), e);
        }
    }

    /**
     * 创建安全的TransformerFactory，配置安全属性防止XXE攻击
     */
    private TransformerFactory createSecureTransformerFactory() {
