import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;

/**
 * XML配置加载服务类
 * 使用XSLT身份转换来标准化XML配置文件
 */
public class ConfigService {

    /**
     * 加载并标准化XML配置文件
     *
     * @param configPath XML配置文件的路径
     * @return 经过身份转换后生成的Document对象
     * @throws IOException 当配置文件不存在或读取失败时抛出
     * @throws TransformerException 当XML转换失败时抛出
     * @throws IllegalArgumentException 当configPath为null或空时抛出
     */
    public Document loadConfig(String configPath) throws IOException, TransformerException {
        // 参数验证
        if (configPath == null || configPath.trim().isEmpty()) {
            throw new IllegalArgumentException("配置文件路径不能为null或空");
        }

        Path path = Paths.get(configPath);

        // 检查文件是否存在
        if (!Files.exists(path) || !Files.isRegularFile(path)) {
            throw new IOException("配置文件不存在或不是文件: " + configPath);
        }

        DocumentBuilderFactory documentBuilderFactory = DocumentBuilderFactory.newInstance();
        DocumentBuilder documentBuilder;
        Document sourceDocument;

        try (InputStream inputStream = Files.newInputStream(path)) {
            // 创建DocumentBuilder并解析XML文件
            documentBuilder = documentBuilderFactory.newDocumentBuilder();
            sourceDocument = documentBuilder.parse(inputStream);
        } catch (ParserConfigurationException e) {
            throw new TransformerException("DocumentBuilder配置失败", e);
        } catch (SAXException e) {
            throw new TransformerException("XML解析失败", e);
        }

        // 创建TransformerFactory和身份转换器
        TransformerFactory transformerFactory = TransformerFactory.newInstance();
        Transformer identityTransformer = transformerFactory.newTransformer(); // 身份转换

        // 创建目标Document
        Document resultDocument;
        try {
            resultDocument = documentBuilderFactory.newDocumentBuilder().newDocument();
        } catch (ParserConfigurationException e) {
            throw new TransformerException("创建结果Document失败", e);
        }

        // 执行身份转换
        DOMSource source = new DOMSource(sourceDocument);
        DOMResult result = new DOMResult(resultDocument);
        identityTransformer.transform(source, result);

        return resultDocument;
    }
}

