import groovy.lang.GroovyClassLoader;
import groovy.lang.Binding;
import org.codehaus.groovy.control.CompilerConfiguration;
import org.codehaus.groovy.control.customizers.SecureASTCustomizer;
import org.codehaus.groovy.control.customizers.ImportCustomizer;
import java.util.Arrays;
import java.util.Collections;
import java.util.concurrent.*;

/**
 * 安全增强的Groovy脚本执行服务。
 */
public class ScriptService {

    // 脚本执行超时时间（秒）
    private static final int SCRIPT_TIMEOUT = 5;

    /**
     * 安全地执行用户提供的Groovy脚本。
     */
    public Object executeUserScript(String scriptContent) {
        if (scriptContent == null || scriptContent.trim().isEmpty()) {
            throw new IllegalArgumentException("Script content cannot be null or empty.");
        }

        // 创建安全配置
        CompilerConfiguration config = createSecureConfig();
        Binding binding = new Binding();

        try (GroovyClassLoader loader = new GroovyClassLoader(this.getClass().getClassLoader(), config)) {
            GroovyShell shell = new GroovyShell(loader, binding, config);

            // 使用线程池控制执行超时
            ExecutorService executor = Executors.newSingleThreadExecutor();
            Future<Object> future = executor.submit(() -> shell.evaluate(scriptContent));

            try {
                return future.get(SCRIPT_TIMEOUT, TimeUnit.SECONDS);
            } catch (TimeoutException e) {
                future.cancel(true);
                throw new SecurityException("Script execution timed out");
            } catch (ExecutionException e) {
                throw new RuntimeException("Script execution failed", e.getCause());
            } finally {
                executor.shutdownNow();
            }
        } catch (Exception e) {
            throw new RuntimeException("Error executing script", e);
        }
    }

    /**
     * 创建安全配置的编译器设置。
     */
    private CompilerConfiguration createSecureConfig() {
        CompilerConfiguration config = new CompilerConfiguration();

        // 安全AST定制器
        SecureASTCustomizer secure = new SecureASTCustomizer();
        secure.setClosuresAllowed(false);

        // 使用token黑名单替代旧API
        secure.setDisallowedTokens(Arrays.asList(
            2,   // PACKAGE_DEF
            3,   // CLASS_DEF
            4    // METHOD_DEF
        ));

        secure.setImportsWhitelist(Arrays.asList(
            "java.math.BigDecimal",
            "java.math.BigInteger",
            "java.util.ArrayList",
            "java.util.HashMap",
            "java.util.LinkedList",
            "java.util.List",
            "java.util.Map"
        ));
        secure.setStarImportsWhitelist(Collections.emptyList());
        secure.setStaticImportsWhitelist(Collections.emptyList());
        secure.setStaticStarImportsWhitelist(Collections.emptyList());

        // 使用接收者黑名单替代setDisallowedMethods
        secure.setDisallowedReceivers(Arrays.asList(
            System.class,
            Runtime.class,
            ProcessBuilder.class
        ));

        // 导入白名单
        ImportCustomizer imports = new ImportCustomizer();
        imports.addStaticStars("java.lang.Math");

        config.addCompilationCustomizers(secure, imports);
        return config;
    }
}

