     * @param sortDirection 排序方向
     * @throws IllegalArgumentException 如果参数无效
     */
    private void validateOrderParameters(String orderBy, String sortDirection) {
        if (orderBy == null || !ALLOWED_ORDER_FIELDS.contains(orderBy.toLowerCase())) {
            throw new IllegalArgumentException("Invalid order field: " + orderBy);
        }

        if (sortDirection == null || !ALLOWED_DIRECTIONS.contains(sortDirection.toUpperCase())) {
            throw new IllegalArgumentException("Invalid sort direction: " + sortDirection);
        }
    }

    /**
     * 获取所有用户
     * @return 用户列表
     */
    public List<User> getAllUsers() {
        return userMapper.getAllUsers();
    }
