        URIBuilder uriBuilder = new URIBuilder(url);
        URI uri = uriBuilder.build();

        // 检查协议
        if (!ALLOWED_SCHEMES.contains(uri.getScheme().toLowerCase())) {
            return null;
        }

        // 检查主机名
        String host = uri.getHost();
        if (host == null || host.isEmpty()) {
