                logger.log(Level.SEVERE, "IO error while fetching URL: " + urlString, e);
                return null;
            }
        } catch (IOException e) {
            logger.log(Level.SEVERE, "Error creating HTTP client for URL: " + urlString, e);
            return null;
        }
