/**
 * 提供从Web上获取页面内容的服务，带有SSRF防护措施。
 */
@Service
public class WebPageFetcherService {

    private final RestTemplate restTemplate;
    private final List<String> allowedDomains;
    private static final Pattern PROTOCOL_PATTERN = Pattern.compile("^https?://.*", Pattern.CASE_INSENSITIVE);

    @Autowired
    public WebPageFetcherService(RestTemplate restTemplate, 
                               @Value("${webpage.fetcher.allowed.domains:example.com,example.org}") String[] allowedDomains) {
        this.restTemplate = restTemplate;
        this.allowedDomains = Arrays.asList(allowedDomains);

        // 配置请求工厂，设置超时和限制响应大小
        SimpleClientHttpRequestFactory requestFactory = new SimpleClientHttpRequestFactory();
        requestFactory.setConnectTimeout(5000); // 5秒连接超时
        requestFactory.setReadTimeout(10000);    // 10秒读取超时
        requestFactory.setBufferRequestBody(false);
        this.restTemplate.setRequestFactory(requestFactory);
    }

    /**
     * 根据提供的URL获取其HTML页面内容，带有SSRF防护。
     *
     * @param url 目标网页的完整URL，例如 "https://www.example.com"
     * @return 返回网页的HTML内容字符串。如果无法访问或发生错误，则返回null。
     * @throws IllegalArgumentException 如果URL不合法或不在允许的域名列表中
     */
    public String getPageContent(String url) {
        if (!StringUtils.hasText(url)) {
            return null;
        }

        // 验证URL格式和协议
        if (!isValidUrl(url)) {
            throw new IllegalArgumentException("Invalid URL or protocol not allowed");
        }

        try {
            // 规范化URL并验证域名
            URI normalizedUri = new URI(url).normalize();
            if (!isDomainAllowed(normalizedUri.getHost())) {
                throw new IllegalArgumentException("Domain not allowed");
            }

            // 从指定的URL获取对象，这里期望得到一个字符串形式的响应体
            return restTemplate.getForObject(normalizedUri.toString(), String.class);
        } catch (URISyntaxException | RestClientException e) {
            System.err.println("Error fetching content from URL: " + url + " - " + e.getMessage());
            return null;
        }
    }

    /**
     * 验证URL是否合法且协议允许
     */
    private boolean isValidUrl(String url) {
        // 检查协议是否为http或https
        if (!PROTOCOL_PATTERN.matcher(url).matches()) {
            return false;
        }

        try {
            URI uri = new URI(url);
            // 基本URL验证
            return uri.getHost() != null && !uri.getHost().isEmpty();
        } catch (URISyntaxException e) {
            return false;
        }
    }

    /**
     * 检查域名是否在白名单中
     */
    private boolean isDomainAllowed(String domain) {
        if (domain == null) {
            return false;
        }

        // 检查直接匹配或子域名匹配
        return allowedDomains.stream()
                .anyMatch(allowed -> 
                    domain.equalsIgnoreCase(allowed) || 
                    domain.endsWith("." + allowed)
                );
    }
}

