import java.io.IOException;
import java.io.InputStream;

/**
 * 配置服务类
 * 用于加载并标准化XML格式的配置文件
 */
public class ConfigService {

    /**
     * 根据用户指定的配置文件路径加载XML配置，并进行身份转换。
     * 该方法会防护XXE攻击，并确保资源正确关闭。
     *
     * @param configPath 配置文件路径
     * @return 标准化后的XML文档对象
     * @throws IllegalArgumentException 当配置路径为空时抛出
     * @throws IOException 当文件不存在或不可读时抛出
     * @throws RuntimeException 当XML处理过程中发生错误时抛出
     */
    public Document loadConfig(String configPath) {
        // 参数校验
        if (configPath == null || configPath.trim().isEmpty()) {
            throw new IllegalArgumentException("配置文件路径不能为空");
        }

        File configFile = new File(configPath);
        if (!configFile.exists() || !configFile.isFile()) {
            throw new IllegalArgumentException("配置文件不存在或不是一个文件: " + configPath);
        }

        // 使用try-with-resources确保资源关闭
        try (InputStream inputStream = new FileInputStream(configFile)) {
            // 配置TransformerFactory以防止XXE攻击
            TransformerFactory factory = TransformerFactory.newInstance();
            factory.setFeature(XMLConstants.FEATURE_SECURE_PROCESSING, true);
            factory.setAttribute(XMLConstants.ACCESS_EXTERNAL_DTD, "");
            factory.setAttribute(XMLConstants.ACCESS_EXTERNAL_STYLESHEET, "");

            // 创建转换器并执行转换
            Transformer transformer = factory.newTransformer();
            StreamSource source = new StreamSource(inputStream);
            DOMResult result = new DOMResult();

            transformer.transform(source, result);

            return (Document) result.getNode();
        } catch (IOException e) {
            throw new RuntimeException("读取配置文件失败: " + configPath, e);
        } catch (Exception e) {
            throw new RuntimeException("XML转换处理失败: " + configPath, e);
        }
    }
}

