    private static final Set<Integer> ALLOWED_PORTS = new HashSet<>(
        Arrays.asList(80, 443, 8080, 8443)
    );

    @Autowired
    public WebPageFetcherService(RestTemplate restTemplate) {
        this.restTemplate = restTemplate;
    }

