import org.xml.sax.SAXException;

import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.parsers.ParserConfigurationException;
import java.io.File;
import java.io.IOException;

/**
 * 配置服务类
 * 用于加载XML格式的配置文件
 */
public class ConfigService {

    // 静态DocumentBuilderFactory实例，避免重复创建
    private static final DocumentBuilderFactory DOCUMENT_BUILDER_FACTORY;

    static {
        DOCUMENT_BUILDER_FACTORY = DocumentBuilderFactory.newInstance();
        // 安全配置：防止XXE攻击
        try {
            DOCUMENT_BUILDER_FACTORY.setFeature("http://apache.org/xml/features/disallow-doctype-decl", true);
            DOCUMENT_BUILDER_FACTORY.setFeature("http://xml.org/sax/features/external-general-entities", false);
            DOCUMENT_BUILDER_FACTORY.setFeature("http://xml.org/sax/features/external-parameter-entities", false);
            DOCUMENT_BUILDER_FACTORY.setXIncludeAware(false);
            DOCUMENT_BUILDER_FACTORY.setExpandEntityReferences(false);
        } catch (ParserConfigurationException e) {
            throw new RuntimeException("XML解析器安全配置失败", e);
        }
    }

    /**
     * 根据用户指定的配置文件路径加载XML配置
     *
     * @param configPath 配置文件路径
     * @return 解析后的XML文档对象
     * @throws IllegalArgumentException 当输入参数无效时
     * @throws IOException 当文件不存在或读取失败时
     * @throws SAXException 当XML格式错误时
     * @throws ParserConfigurationException 当解析器配置错误时
     */
    public Document loadConfig(String configPath) 
            throws IllegalArgumentException, IOException, SAXException, ParserConfigurationException {
        // 参数校验
        if (configPath == null || configPath.trim().isEmpty()) {
            throw new IllegalArgumentException("配置文件路径不能为空");
        }

        File configFile = new File(configPath);
        if (!configFile.exists()) {
            throw new IOException("配置文件不存在: " + configPath);
