        if (!configFile.isFile()) {
            throw new IOException("指定路径不是文件: " + configPath);
        }

        // 创建DocumentBuilder（线程安全：每次调用创建新实例）
        DocumentBuilder builder = DOCUMENT_BUILDER_FACTORY.newDocumentBuilder();

        // 解析XML文件
        return builder.parse(configFile);
    }
}

