import java.io.IOException;
import java.io.InputStream;

public class ConfigService {

    public Document loadConfig(String configPath) throws Exception {
        // 验证输入参数
        if (configPath == null || configPath.trim().isEmpty()) {
            throw new IllegalArgumentException("Config path cannot be null or empty");
        }

        File configFile = new File(configPath);
        if (!configFile.exists()) {
            throw new IOException("Config file not found: " + configPath);
        }

        // 创建安全的SAXParserFactory
        SAXParserFactory saxFactory = SAXParserFactory.newInstance();
        try {
            // 安全配置：防止XXE攻击
            saxFactory.setFeature("http://apache.org/xml/features/disallow-doctype-decl", true);
            saxFactory.setFeature("http://xml.org/sax/features/external-general-entities", false);
            saxFactory.setFeature("http://xml.org/sax/features/external-parameter-entities", false);
            saxFactory.setFeature("http://apache.org/xml/features/nonvalidating/load-external-dtd", false);
            saxFactory.setXIncludeAware(false);
        } catch (ParserConfigurationException e) {
            throw new Exception("Failed to configure secure XML parser", e);
        }

        // 创建安全的TransformerFactory
        TransformerFactory transformerFactory = TransformerFactory.newInstance();
        try {
            transformerFactory.setFeature(XMLConstants.FEATURE_SECURE_PROCESSING, true);
        } catch (TransformerException e) {
            throw new Exception("Failed to configure secure transformer", e);
        }

        try (InputStream inputStream = new FileInputStream(configFile)) {
            // 创建SAX解析器
            SAXParser saxParser = saxFactory.newSAXParser();
            XMLReader xmlReader = saxParser.getXMLReader();

            // 创建安全的DocumentBuilder
            DocumentBuilderFactory docFactory = DocumentBuilderFactory.newInstance();
            docFactory.setFeature("http://apache.org/xml/features/disallow-doctype-decl", true);
            docFactory.setFeature("http://xml.org/sax/features/external-general-entities", false);
            docFactory.setFeature("http://xml.org/sax/features/external-parameter-entities", false);
            docFactory.setFeature("http://apache.org/xml/features/nonvalidating/load-external-dtd", false);
            docFactory.setXIncludeAware(false);
            docFactory.setExpandEntityReferences(false);
            DocumentBuilder docBuilder = docFactory.newDocumentBuilder();
            Document document = docBuilder.newDocument();

            // 使用Transformer将SAX事件转换为DOM
            Transformer transformer = transformerFactory.newTransformer();
            SAXSource source = new SAXSource(xmlReader, new InputSource(inputStream));
            DOMResult result = new DOMResult(document);
            transformer.transform(source, result);

            return document;
        } catch (SAXException e) {
            throw new Exception("XML parsing error: " + e.getMessage(), e);
        } catch (IOException e) {
            throw new Exception("I/O error while reading config file: " + e.getMessage(), e);
        } catch (ParserConfigurationException e) {
            throw new Exception("Parser configuration error: " + e.getMessage(), e);
        } catch (TransformerException e) {
            throw new Exception("Transformation error: " + e.getMessage(), e);
        }
    }
}
