            LOGGER.log(Level.WARNING, "Invalid URI syntax: " + resourceUrl, e);
            return null;
        } catch (IOException e) {
            LOGGER.log(Level.WARNING, "Failed to fetch resource: " + resourceUrl, e);
            return null;
        }
    }

    /**
     * 获取内容，处理重定向。
     */
    private byte[] fetchContentWithRedirects(URL url, int redirectCount) throws IOException {
        if (redirectCount > MAX_REDIRECTS) {
            throw new IOException("Maximum redirects (" + MAX_REDIRECTS + ") exceeded");
        }

        HttpURLConnection connection = null;
        InputStream inputStream = null;
        ByteArrayOutputStream outputStream = null;

        try {
            connection = (HttpURLConnection) url.openConnection();
            connection.setConnectTimeout(CONNECT_TIMEOUT);
            connection.setReadTimeout(READ_TIMEOUT);
            connection.setInstanceFollowRedirects(false); // 我们手动处理重定向

            int responseCode = connection.getResponseCode();

            // 处理重定向
            if (responseCode == HttpURLConnection.HTTP_MOVED_PERM || 
                responseCode == HttpURLConnection.HTTP_MOVED_TEMP || 
                responseCode == HttpURLConnection.HTTP_SEE_OTHER) {
                String newUrl = connection.getHeaderField("Location");
                if (newUrl == null) {
                    throw new IOException("Redirect with no Location header");
                }
                return fetchContentWithRedirects(new URL(newUrl), redirectCount + 1);
            }

            // 检查响应码
            if (responseCode != HttpURLConnection.HTTP_OK) {
                throw new IOException("Server returned HTTP response code: " + responseCode);
            }

            // 读取内容
            inputStream = connection.getInputStream();
            outputStream = new ByteArrayOutputStream();

            byte[] buffer = new byte[4096];
            int bytesRead;
            while ((bytesRead = inputStream.read(buffer)) != -1) {
                outputStream.write(buffer, 0, bytesRead);
            }

            return outputStream.toByteArray();
        } finally {
            // 确保资源被关闭
            if (inputStream != null) {
                try {
                    inputStream.close();
                } catch (IOException e) {
