                }
            }
            if (outputStream != null) {
                try {
                    outputStream.close();
                } catch (IOException e) {
                    LOGGER.log(Level.WARNING, "Failed to close output stream", e);
                }
            }
            if (connection != null) {
                connection.disconnect();
            }
        }
    }
