            // 验证协议
            String scheme = uri.getScheme();
            if (scheme == null || !ALLOWED_PROTOCOLS.contains(scheme.toLowerCase())) {
                return null;
            }

            // 验证主机名
            String host = uri.getHost();
            if (host == null) {
                return null;
            }

            // 检查主机名是否为IP地址
            if (isIpAddress(host)) {
                // 如果是IP地址，直接检查是否在禁止范围内
                if (isForbiddenIp(host)) {
                    return null;
                }
            } else {
                // 如果是域名，解析为IP地址并检查
                InetAddress[] addresses = InetAddress.getAllByName(host);
                for (InetAddress address : addresses) {
                    if (isForbiddenIp(address.getHostAddress())) {
                        return null;
                    }
                }
            }

            // 使用安全的连接方式获取资源
            return fetchResourceSafely(uri);
        } catch (URISyntaxException | IOException e) {
            // 在实际应用中，这里会记录日志
            System.err.println("Failed to fetch resource: " + e.getMessage());
            return null;
        }
