        if (ipAddress.contains("::")) {
            ipAddress = expandIPv6(ipAddress);
        }

        for (String range : FORBIDDEN_IP_RANGES) {
            if (isIpInRange(ipAddress, range)) {
                return true;
            }
        }
        return false;
    }

    /**
     * 检查IP地址是否在指定范围内
     */
    private boolean isIpInRange(String ipAddress, String cidr) {
        String[] parts = cidr.split("/");
        String networkAddress = parts[0];
        int prefixLength;

        try {
            prefixLength = Integer.parseInt(parts[1]);
        } catch (ArrayIndexOutOfBoundsException e) {
            // 如果没有前缀长度，则精确匹配
            return ipAddress.equals(networkAddress);
        }

        // 处理IPv4地址
        if (ipAddress.contains(".")) {
            return isIPv4InRange(ipAddress, networkAddress, prefixLength);
        } 
        // 处理IPv6地址
        else if (ipAddress.contains(":")) {
            return isIPv6InRange(ipAddress, networkAddress, prefixLength);
        }

        return false;
    }

    /**
     * 检查IPv4地址是否在指定范围内
     */
    private boolean isIPv4InRange(String ipAddress, String networkAddress, int prefixLength) {
        try {
            byte[] ipBytes = InetAddress.getByName(ipAddress).getAddress();
            byte[] networkBytes = InetAddress.getByName(networkAddress).getAddress();

            int mask = 0xFFFFFFFF << (32 - prefixLength);

            int ip = ((ipBytes[0] & 0xFF) << 24) | 
                     ((ipBytes[1] & 0xFF) << 16) | 
                     ((ipBytes[2] & 0xFF) << 8) | 
                     (ipBytes[3] & 0xFF);

            int network = ((networkBytes[0] & 0xFF) << 24) | 
                          ((networkBytes[1] & 0xFF) << 16) | 
                          ((networkBytes[2] & 0xFF) << 8) | 
                          (networkBytes[3] & 0xFF);

            return (ip & mask) == (network & mask);
        } catch (UnknownHostException e) {
            return false;
        }
    }

    /**
     * 检查IPv6地址是否在指定范围内
     */
    private boolean isIPv6InRange(String ipAddress, String networkAddress, int prefixLength) {
        try {
            byte[] ipBytes = InetAddress.getByName(ipAddress).getAddress();
            byte[] networkBytes = InetAddress.getByName(networkAddress).getAddress();

            if (prefixLength > 128) {
                return false;
            }

            int fullBytes = prefixLength / 8;
            int remainingBits = prefixLength % 8;

            // 比较完整字节
            for (int i = 0; i < fullBytes; i++) {
                if (ipBytes[i] != networkBytes[i]) {
                    return false;
                }
            }

            // 比较剩余位
            if (remainingBits > 0 && fullBytes < 16) {
                byte mask = (byte) (0xFF << (8 - remainingBits));
                if ((ipBytes[fullBytes] & mask) != (networkBytes[fullBytes] & mask)) {
                    return false;
                }
            }

            return true;
        } catch (UnknownHostException e) {
            return false;
        }
    }

    /**
     * 展开缩写的IPv6地址
     */
    private String expandIPv6(String ipAddress) {
        if (!ipAddress.contains("::")) {
            return ipAddress;
        }

        String[] parts = ipAddress.split("::");
        String[] firstPart = parts[0].split(":");
        String[] secondPart = parts.length > 1 ? parts[1].split(":") : new String[0];

        int missingGroups = 8 - (firstPart.length + secondPart.length);
        String[] missing = new String[missingGroups];
        Arrays.fill(missing, "0");

        StringBuilder result = new StringBuilder();

        for (String group : firstPart) {
            if (!group.isEmpty()) {
                result.append(group).append(":");
            }
        }

        for (String group : missing) {
            result.append(group).append(":");
        }

        for (String group : secondPart) {
            if (!group.isEmpty()) {
                result.append(group).append(":");
            }
        }

        // 移除最后的冒号
        if (result.length() > 0) {
            result.setLength(result.length() - 1);
        }

        return result.toString();
    }
}

