import org.apache.http.client.methods.HttpGet;
import org.apache.http.impl.client.CloseableHttpClient;
import org.apache.http.impl.client.HttpClients;
import org.apache.http.util.EntityUtils;

import java.io.IOException;
import java.net.*;
import java.util.Arrays;
import java.util.HashSet;
import java.util.Set;

/**
 * 安全的URL内容获取服务，防止SSRF攻击
 */
public class UrlService {

    // 连接超时时间（毫秒）
    private static final int CONNECT_TIMEOUT = 10000;
    // 读取超时时间（毫秒）
    private static final int SOCKET_TIMEOUT = 30000;

    // 协议白名单
    private static final Set<String> ALLOWED_SCHEMES = new HashSet<>(Arrays.asList("http", "https"));

    /**
     * 获取指定URL的内容（安全版本）
     *
     * @param userUrl 用户提供的URL字符串
     * @return 响应体内容字符串，如果发生错误则返回null
     */
    public String fetchUrlContent(String userUrl) {
        if (userUrl == null || userUrl.trim().isEmpty()) {
            return null;
        }

        try {
            URL url = new URL(userUrl.trim());
            // 校验协议
            if (!ALLOWED_SCHEMES.contains(url.getProtocol().toLowerCase())) {
                throw new IllegalArgumentException("Unsupported URL scheme");
            }

            // 校验目标host是否是内网ip
            if (isPrivateIP(url.getHost())) {
                throw new IllegalArgumentException("Access to private IP is not allowed");
            }

            // 发起HTTP请求
            return executeHttpGet(url.toString());

        } catch (MalformedURLException e) {
            // URL格式错误
            return null;
        } catch (IllegalArgumentException e) {
            // 协议或IP不允许
            return null;
        } catch (IOException e) {
            // 网络IO异常
            return null;
        }
    }

    private String executeHttpGet(String url) throws IOException {
        try (CloseableHttpClient httpClient = HttpClients.custom()
                // 禁用自动重定向，防止跳转到内网
                .disableRedirectHandling()
                .build()) {

            RequestConfig config = RequestConfig.custom()
                    .setConnectTimeout(CONNECT_TIMEOUT)
                    .setSocketTimeout(SOCKET_TIMEOUT)
                    .build();

            HttpGet httpGet = new HttpGet(url);
            httpGet.setConfig(config);

            try (CloseableHttpResponse response = httpClient.execute(httpGet)) {
                // 检查响应状态码
                if (response.getStatusLine().getStatusCode() != HttpStatus.SC_OK) {
                    return null;
                }
                HttpEntity entity = response.getEntity();
                return entity != null ? EntityUtils.toString(entity, "UTF-8") : null;
            }
        }
    }

    /**
     * 判断host是否是内网IP
     */
    private boolean isPrivateIP(String host) {
        try {
            InetAddress address = InetAddress.getByName(host);
            return address.isAnyLocalAddress() ||
                   address.isLoopbackAddress() ||
                   address.isSiteLocalAddress() ||
                   isIPv6Private(address);
        } catch (UnknownHostException e) {
            return true; // 解析失败默认拒绝
        }
    }

    /**
     * 判断 IPv6 地址是否为私有地址
     */
    private boolean isIPv6Private(InetAddress address) {
        if (!(address instanceof Inet6Address)) {
            return false;
        }
        byte[] bytes = address.getAddress();
        // fc00::/7 Unique local address
        return (bytes[0] & (byte) 0xfe) == (byte) 0xfc;
    }
}

