import java.util.logging.Logger;

/**
 * URL内容获取服务
 */
public class UrlService {

    private static final Logger LOGGER = Logger.getLogger(UrlService.class.getName());

    // 连接超时时间（毫秒）
    private static final int CONNECT_TIMEOUT = 10_000;
    // 读取超时时间（毫秒）
    private static final int SOCKET_TIMEOUT = 30_000;
    // 从连接池获取连接的超时时间（毫秒）
    private static final int CONNECTION_REQUEST_TIMEOUT = 5_000;

    // 复用HttpClient实例，减少创建开销
    private static final CloseableHttpClient HTTP_CLIENT = HttpClients.createDefault();

    /**
     * 获取指定URL的内容
     *
     * @param userUrl 用户提供的URL字符串
     * @return 响应体内容字符串，如果发生错误则返回null
     */
    public String fetchUrlContent(String userUrl) {
        if (userUrl == null || userUrl.trim().isEmpty()) {
            LOGGER.warning("URL为空");
            return null;
        }

        URI uri;
        try {
            uri = new URI(userUrl.trim());
            // 基础协议检查，只允许 http/https
            String scheme = uri.getScheme();
            if (scheme == null || !(scheme.equalsIgnoreCase("http") || scheme.equalsIgnoreCase("https"))) {
                LOGGER.warning("不支持的协议类型: " + scheme);
                return null;
            }
            // 可选：禁止访问127.0.0.1、localhost、内网等敏感地址，防止SSRF
        } catch (URISyntaxException e) {
            LOGGER.log(Level.WARNING, "URL格式无效: " + userUrl, e);
            return null;
        }

        // 配置请求超时
        RequestConfig config = RequestConfig.custom()
                .setConnectTimeout(CONNECT_TIMEOUT)
                .setSocketTimeout(SOCKET_TIMEOUT)
                .setConnectionRequestTimeout(CONNECTION_REQUEST_TIMEOUT)
                .build();

        HttpGet httpGet = new HttpGet(uri);
        httpGet.setConfig(config);
        // 设置User-Agent防止被部分站点拒绝
        httpGet.addHeader("User-Agent", "Mozilla/5.0 (Java URLService)");

        try (CloseableHttpResponse response = HTTP_CLIENT.execute(httpGet)) {

            int statusCode = response.getStatusLine().getStatusCode();
            if (statusCode != HttpStatus.SC_OK) {
                LOGGER.warning("请求失败，状态码: " + statusCode);
                return null;
            }

            HttpEntity entity = response.getEntity();
            if (entity == null) {
                LOGGER.warning("响应内容为空");
                return null;
            }

            // 优先按响应中的ContentType编码解析，否则使用UTF-8
            String content = EntityUtils.toString(entity, StandardCharsets.UTF_8);
            return content;

        } catch (IOException e) {
            LOGGER.log(Level.WARNING, "获取URL内容失败: " + userUrl, e);
            return null;
        }
    }

    /**
     * 关闭HttpClient（在应用退出时调用）
     */
    public static void shutdown() {
        try {
            HTTP_CLIENT.close();
        } catch (IOException e) {
            LOGGER.log(Level.WARNING, "关闭HttpClient失败", e);
        }
    }
}

