 * Mapper interface for managing Product data with MyBatis annotations.
 */
public interface ProductMapper {

    /**
     * Inserts a new product into the database.
     * @param product the product to be inserted
     * @return number of rows affected
     */
    @Insert("INSERT INTO products (name) VALUES (#{name})")
    @Options(useGeneratedKeys = true, keyProperty = "id")
    int insert(Product product);

    /**
     * Updates the name of an existing product based on its ID.
     * @param product the product with updated name and its ID
     * @return number of rows affected
     */
    @Update("UPDATE products SET name = #{name} WHERE id = #{id}")
    int update(Product product);

    /**
     * Deletes a product by its name.
     * @param name the name of the product to be deleted
     * @return number of rows affected
     */
    @Delete("DELETE FROM products WHERE name = #{name}")
    int delete(@Param("name") String name);

    /**
     * Selects products by exact name match.
     * @param name the name of the products to retrieve
     * @return list of products matching the given name
     */
    @Select("SELECT id, name FROM products WHERE name = #{name}")
    List<Product> select(@Param("name") String name);
}
