        String[] dates = dateRange.split(",");
        if (dates.length != 2) {
            throw new IllegalArgumentException("dateRange must be in format 'startDate,endDate'");
        }

        String startDate = dates[0].trim();
        String endDate = dates[1].trim();

        if (startDate.isEmpty() || endDate.isEmpty()) {
            throw new IllegalArgumentException("startDate and endDate cannot be empty");
        }

        return salesMapper.getSalesByGroup(groupByField, startDate, endDate);
    }
}
