import javax.xml.parsers.SAXParserFactory;
import javax.xml.transform.Transformer;
import javax.xml.transform.TransformerFactory;
import javax.xml.transform.dom.DOMResult;
import javax.xml.transform.sax.SAXSource;
import org.xml.sax.InputSource;
import org.xml.sax.XMLReader;
import java.io.File;
import java.io.FileInputStream;
import java.io.IOException;

/**
 * 配置服务类
 * 用于加载XML格式的配置文件
 */
public class ConfigService {

    /**
     * 根据用户指定的配置文件路径加载XML配置
     *
     * @param configPath 配置文件路径
     * @return 解析后的XML文档对象
     * @throws Exception 当文件不存在或解析失败时抛出异常
     */
    public Document loadConfig(String configPath) throws Exception {
        if (configPath == null || configPath.trim().isEmpty()) {
            throw new IllegalArgumentException("配置文件路径不能为空");
        }

        File configFile = new File(configPath);
        if (!configFile.exists() || !configFile.isFile()) {
            throw new IOException("配置文件不存在或不是一个文件: " + configPath);
        }

        try {
            // 创建SAXParserFactory实例并设置安全特性以防止XXE攻击
            SAXParserFactory factory = SAXParserFactory.newInstance();
            factory.setFeature("http://apache.org/xml/features/disallow-doctype-decl", true);
            factory.setFeature("http://xml.org/sax/features/external-general-entities", false);
            factory.setFeature("http://xml.org/sax/features/external-parameter-entities", false);
            factory.setFeature("http://apache.org/xml/features/nonvalidating/load-external-dtd", false);
            SAXParser parser = factory.newSAXParser();
            XMLReader reader = parser.getXMLReader();

            // 创建TransformerFactory实例并设置安全特性以防止XXE攻击
            TransformerFactory transformerFactory = TransformerFactory.newInstance();
            transformerFactory.setFeature("http://javax.xml.XMLConstants/feature/secure-processing", true);
            transformerFactory.setAttribute("http://javax.xml.XMLConstants/property/accessExternalDTD", "");
            transformerFactory.setAttribute("http://javax.xml.XMLConstants/property/accessExternalStylesheet", "");
            Transformer transformer = transformerFactory.newTransformer();

            SAXSource source = new SAXSource(reader, new InputSource(new FileInputStream(configFile)));
            DOMResult result = new DOMResult();

            transformer.transform(source, result);

            return (Document) result.getNode();
        } catch (Exception e) {
            throw new Exception("XML解析失败: " + e.getMessage(), e);
        }
    }
}

