import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.ArrayList;
import java.util.List;
import java.util.zip.ZipEntry;
import java.util.zip.ZipInputStream;

/**
 * ZIP文件解压服务类
 */
public class ZipService {

    /**
     * 解压ZIP文件到指定目录
     * 
     * @param zipFilePath ZIP文件路径
     * @param extractPath 解压目标路径
     * @return 解压出的文件绝对路径列表
     * @throws IOException 当发生IO异常时抛出
     */
    public List<String> extractZipFile(String zipFilePath, String extractPath) throws IOException {
        List<String> extractedFiles = new ArrayList<>();

        // 检查ZIP文件是否存在
        File zipFile = new File(zipFilePath);
        if (!zipFile.exists()) {
            throw new IOException("ZIP文件不存在: " + zipFilePath);
        }

        // 创建解压目录（如果不存在）
        Path extractPathObj = Paths.get(extractPath);
        if (!Files.exists(extractPathObj)) {
            Files.createDirectories(extractPathObj);
        }

        // 解压ZIP文件
        try (FileInputStream fis = new FileInputStream(zipFile);
             ZipInputStream zis = new ZipInputStream(fis)) {

            ZipEntry entry;
            while ((entry = zis.getNextEntry()) != null) {
                String fileName = entry.getName();
                File entryFile = new File(extractPath, fileName);

                // 防止路径遍历漏洞 (Zip Slip)
                String canonicalDestPath = extractPathObj.toFile().getCanonicalPath();
                String canonicalEntryPath = entryFile.getCanonicalPath();
                if (!canonicalEntryPath.startsWith(canonicalDestPath + File.separator)) {
                    throw new IOException("非法的文件路径: " + fileName);
                }

                if (entry.isDirectory()) {
                    // 创建目录
                    entryFile.mkdirs();
                } else {
                    // 创建父目录
                    File parentFile = entryFile.getParentFile();
                    if (parentFile != null && !parentFile.exists()) {
                        parentFile.mkdirs();
                    }

                    // 解压文件
                    try (FileOutputStream fos = new FileOutputStream(entryFile)) {
                        byte[] buffer = new byte[1024];
                        int length;
                        while ((length = zis.read(buffer)) > 0) {
                            fos.write(buffer, 0, length);
                        }
                    }

                    // 添加到结果列表
                    extractedFiles.add(entryFile.getAbsolutePath());
                }

                // 关闭当前entry
                zis.closeEntry();
            }
        }

        return extractedFiles;
    }
}
