     */
    private void validateGroupByField(String groupByField) {
        if (groupByField == null || groupByField.trim().isEmpty()) {
            throw new IllegalArgumentException("分组字段不能为空");
        }

        String trimmedField = groupByField.trim().toLowerCase();
        if (!ALLOWED_GROUP_FIELDS.contains(trimmedField)) {
            String allowedFields = ALLOWED_GROUP_FIELDS.stream()
                .collect(Collectors.joining(", "));
            throw new IllegalArgumentException(
                "不支持的分组字段: " + groupByField + 
                "。允许的分组字段: " + allowedFields);
        }
    }

    /**
