import org.w3c.dom.Document;
import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.parsers.ParserConfigurationException;
import java.io.File;
import java.io.IOException;
import java.io.InputStream;
import java.nio.file.Files;

/**
 * 配置服务类
 * 用于加载 XML 格式的配置文件。
 *
 * <p>实现要点：
 * <ul>
 *   <li>使用线程安全的 DocumentBuilderFactory 实例，避免每次创建新工厂带来的开销。</li>
 *   <li>开启外部实体（XXE）防护，防止 XML 外部实体注入攻击。</li>
 *   <li>对空路径、空文件、不存在的文件提前做校验，抛出清晰异常。</li>
 *   <li>使用 try-with-resources 自动关闭文件流。</li>
 *   <li>所有异常统一包装为 IllegalArgumentException / IOException / ParserConfigurationException /
 *       org.xml.sax.SAXException，方便调用方捕获与处理。</li>
 * </ul>
 */
public class ConfigService {

    // 线程安全的工厂实例，可复用
    private static final DocumentBuilderFactory FACTORY = createSecureDocumentBuilderFactory();

    /**
     * 根据用户指定的配置文件路径加载 XML 配置
     *
     * @param configPath 配置文件路径，不能为空或空白
     * @return 解析后的 XML 文档对象
     * @throws IllegalArgumentException 当路径为空或空白
     * @throws IOException              当文件不存在、不可读或为空
     * @throws ParserConfigurationException 当解析器配置异常
     * @throws org.xml.sax.SAXException     当 XML 格式错误
     */
    public Document loadConfig(String configPath) throws IOException,
            ParserConfigurationException, org.xml.sax.SAXException {

        if (configPath == null || configPath.trim().isEmpty()) {
            throw new IllegalArgumentException("配置文件路径不能为空");
        }

        File configFile = new File(configPath);
        if (!configFile.isFile()) {
            throw new IOException("配置文件不存在或不是一个文件: " + configPath);
        }
        if (!configFile.canRead()) {
            throw new IOException("配置文件不可读: " + configPath);
        }
        if (configFile.length() == 0) {
            throw new IOException("配置文件为空: " + configPath);
        }

        // 使用复用的工厂创建解析器，避免重复创建
        DocumentBuilder builder = FACTORY.newDocumentBuilder();
        // Files.newInputStream 会在 try-with-resources 中自动关闭
        try (InputStream in = Files.newInputStream(configFile.toPath())) {
            return builder.parse(in);
        }
    }

    /**
     * 创建并配置安全的 DocumentBuilderFactory，禁用外部实体以防止 XXE 攻击
     */
    private static DocumentBuilderFactory createSecureDocumentBuilderFactory() {
        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        factory.setNamespaceAware(true);

        // 禁用外部实体
        try {
            factory.setFeature("http://apache.org/xml/features/disallow-doctype-decl", true);
            factory.setFeature("http://xml.org/sax/features/external-general-entities", false);
            factory.setFeature("http://xml.org/sax/features/external-parameter-entities", false);
            factory.setFeature("http://apache.org/xml/features/nonvalidating/load-external-dtd", false);
        } catch (ParserConfigurationException e) {
            throw new RuntimeException("无法配置安全的XML解析器", e);
        }

        // 禁止扩展实体
        factory.setXIncludeAware(false);
        factory.setExpandEntityReferences(false);

        return factory;
    }
}

