import javax.xml.parsers.ParserConfigurationException;
import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;

/**
 * 配置服务类
 * 用于加载 XML 格式的配置文件
 */
public class ConfigService {

    private static final int MAX_FILE_SIZE = 10 * 1024 * 1024; // 10 MB

    /**
     * 根据用户指定的配置文件路径加载 XML 配置
     *
     * @param configPath 配置文件路径
     * @return 解析后的 XML 文档对象
     * @throws IllegalArgumentException 当路径为空或无效时
     * @throws IOException              当文件不存在、不可读或过大时
     * @throws ParserConfigurationException 当解析器配置失败时
     * @throws org.xml.sax.SAXException 当 XML 格式错误时
     */
    public Document loadConfig(String configPath) throws IOException, ParserConfigurationException, org.xml.sax.SAXException {
        if (configPath == null || configPath.trim().isEmpty()) {
            throw new IllegalArgumentException("配置文件路径不能为空");
        }

        Path path = new File(configPath).toPath();
        if (!Files.isRegularFile(path) || !Files.isReadable(path)) {
            throw new IOException("配置文件不存在或不可读: " + configPath);
        }

        long size = Files.size(path);
        if (size > MAX_FILE_SIZE) {
            throw new IOException("配置文件过大，最大允许 " + MAX_FILE_SIZE / (1024 * 1024) + " MB");
        }

        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        // 禁用外部实体，防止 XXE 攻击
        factory.setFeature(XMLConstants.FEATURE_SECURE_PROCESSING, true);
        factory.setFeature("http://apache.org/xml/features/disallow-doctype-decl", true);
        factory.setNamespaceAware(true);
        factory.setIgnoringElementContentWhitespace(true);

        DocumentBuilder builder = factory.newDocumentBuilder();
        return builder.parse(path.toFile());
    }
}

