        Path baseDir = Paths.get(BASE_DIR).toAbsolutePath().normalize();

        // 用户传入的文件路径
        Path inputPath;
        try {
            inputPath = Paths.get(filePath).normalize();
        } catch (Exception e) {
            throw new IOException("无效的文件路径: " + filePath);
        }

        // 检查输入路径是否包含非法字符或试图跳出目录
        if (inputPath.isAbsolute() || filePath.contains("..")) {
            throw new IOException("不允许访问该路径: " + filePath);
        }

        // 拼接完整路径
        Path targetPath = baseDir.resolve(inputPath).normalize();

        // 关键安全检查：确保目标路径在基础目录内
        if (!targetPath.startsWith(baseDir)) {
            throw new IOException("不允许访问该路径: " + filePath);
        }

        // 检查文件是否存在且是普通文件
        if (!Files.exists(targetPath) || !Files.isRegularFile(targetPath)) {
            throw new IOException("文件不存在或路径无效: " + filePath);
        }

        // 读取文件内容
        StringJoiner contentJoiner = new StringJoiner(System.lineSeparator());

        try (BufferedReader reader = Files.newBufferedReader(targetPath, StandardCharsets.UTF_8)) {
