import javax.xml.parsers.DocumentBuilderFactory;
import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Paths;

public class ConfigService {

    /**
     * Loads and parses an XML configuration file into a DOM Document object.
     *
     * @param configPath the path to the XML configuration file
     * @return a Document object representing the parsed XML content
     * @throws Exception if the XML file cannot be read, parsed, or is malformed
     */
    public Document loadConfig(String configPath) throws Exception {
        // Check for null or empty input
        if (configPath == null || configPath.trim().isEmpty()) {
            throw new IllegalArgumentException("Configuration file path cannot be null or empty");
        }

        // Check if file exists
        File configFile = new File(configPath);
        if (!configFile.exists()) {
            throw new IOException("Configuration file does not exist: " + configPath);
        }

        try {
            // Create DocumentBuilderFactory
            DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();

            // Configure factory for security and namespace awareness
            factory.setNamespaceAware(true);
            factory.setValidating(false);

            // Prevent XXE attacks
            factory.setFeature("http://apache.org/xml/features/disallow-doctype-decl", true);
            factory.setFeature("http://xml.org/sax/features/external-general-entities", false);
            factory.setFeature("http://xml.org/sax/features/external-parameter-entities", false);

            // Create DocumentBuilder
            DocumentBuilder builder = factory.newDocumentBuilder();

            // Read XML content from file
            String content = new String(Files.readAllBytes(Paths.get(configPath)));

            // Parse the XML content into a Document object
            Document document = builder.parse(new InputSource(new java.io.StringReader(content)));

