public class MessageService {

    public String getWelcomeMessage(String username) {
        if (username == null) {
            username = "World";
        }
        return "<h1>Welcome, " + escapeHtml(username) + "!</h1>";
    }

    private String escapeHtml(String input) {
        if (input == null) {
            return null;
        }
        return input.replace("&", "&amp;")
                   .replace("<", "&lt;")
                   .replace(">", "&gt;")
                   .replace("\"", "&quot;")
                   .replace("'", "&#x27;");
    }
}
