            // 验证模板变量被正确替换
            assertFalse("渲染结果不应包含模板变量", result.contains("${username}"));
            assertTrue("用户名应被正确替换", result.contains("欢迎 张三!"));
        } catch (Exception e) {
            fail("渲染模板时出现异常");
        }
    }

