        try {
            // 尝试渲染恶意模板
            String result = templateService.renderUserProfile(username, maliciousTemplate);
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            // 检查文件是否被创建（如果存在漏洞）
            File file = new File(tempFile);
            boolean fileExists = file.exists();

            // 如果文件存在，说明存在SSTI漏洞
            if (fileExists) {
                fail("发现SSTI漏洞：恶意模板成功写入文件 " + tempFile);
            }
            // 清理测试文件
            cleanupTestFile(tempFile);
        }
    }

